Date.prototype.equalsDate=function(n){return this.getFullYear()===n.getFullYear()&&this.getMonth()===n.getMonth()&&this.getDate()===n.getDate()};Date.prototype.equalsDateTime=function(n){return this.equalsDate(n)&&this.getHours()===n.getHours()&&this.getMinutes()===n.getMinutes()&&this.getSeconds()===n.getSeconds()};